<style lang="css" scoped>
    .qrcode-container {
        width: 5.8rem;
        height: auto;
        position: absolute;
        top: 2rem;
        left: 11rem;
        padding: 0.15rem;
        background: url(data:image/png;base64,);
        background-size: 100% 100%;
        background-repeat: no-repeat;
    }
</style>

<template>
    <div id="qrcode" class="qrcode-container">
        <login-qrcode v-if="!isLogined" @userLoginSuccess="handleUserLoginSuccess"></login-qrcode>
        <auto-renew-qrcode v-else></auto-renew-qrcode>
    </div>
</template>

<script>
    import LoginQrcode from '../../../components/LoginQrcode.vue';
    import AutoRenewQrcode from './AutoRenewQrcode.vue';

    export default {
        name: 'QrcodeContainer',
        components: {
            LoginQrcode,
            AutoRenewQrcode
        },
        // 属性、state
        props: {},
        data () {
            return {
                isLogined: false
            }
        },
        // 生命周期钩子
        created() {
        },
        // computed、methods等
        methods: {
            handleUserLoginSuccess() {
                this.isLogined = true;
            }
        }
    }

</script>